SELECT * FROM comp_cast_type AS cct2, aka_name AS an, complete_cast AS cc, cast_info AS ci WHERE cct2.kind = 'complete+verified' AND ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;