SELECT * FROM person_info AS pi, cast_info AS ci, aka_name AS an, role_type AS rt WHERE ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND rt.role = 'actress' AND rt.id = ci.role_id AND ci.role_id = rt.id AND ci.person_id = an.person_id AND an.person_id = ci.person_id AND ci.person_id = pi.person_id AND pi.person_id = ci.person_id;