SELECT * FROM company_name AS cn, complete_cast AS cc, movie_companies AS mc, title AS t, comp_cast_type AS cct2 WHERE cct2.kind = 'complete+verified' AND cn.country_code = '[us]' AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;