SELECT * FROM char_name AS chn, person_info AS pi, cast_info AS ci, complete_cast AS cc, role_type AS rt WHERE chn.name = 'Queen' AND ci.note IN ('(voice)', '(voice) (uncredited)', '(voice: English version)') AND rt.role = 'actress' AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND rt.id = ci.role_id AND ci.role_id = rt.id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND ci.person_id = pi.person_id AND pi.person_id = ci.person_id;