SELECT * FROM keyword AS k, movie_keyword AS mk, company_name AS cn, complete_cast AS cc, movie_companies AS mc WHERE cn.country_code = '[us]' AND k.keyword = 'computer-animation' AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;