SELECT * FROM movie_companies AS mc, complete_cast AS cc, movie_keyword AS mk, title AS t WHERE t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND mc.movie_id = cc.movie_id AND cc.movie_id = mc.movie_id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id;