SELECT * FROM title AS t, movie_companies AS mc, company_name AS cn, movie_keyword AS mk WHERE cn.country_code = '[us]' AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id;