SELECT * FROM info_type AS it, title AS t, movie_keyword AS mk, movie_info AS mi WHERE it.info = 'release dates' AND mi.info LIKE 'USA:%200%' AND t.title = 'Shrek 2' AND t.production_year BETWEEN 2000 AND 2005 AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mi.movie_id = mk.movie_id AND mk.movie_id = mi.movie_id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;