SELECT * FROM comp_cast_type AS cct2, complete_cast AS cc, cast_info AS ci WHERE cct2.kind = 'complete+verified' AND ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;