SELECT * FROM title AS t, keyword AS k, complete_cast AS cc, movie_keyword AS mk, comp_cast_type AS cct2 WHERE cct2.kind = 'complete+verified' AND k.keyword = 'computer-animation' AND t.production_year BETWEEN 2000 AND 2010 AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND mk.movie_id = cc.movie_id AND cc.movie_id = mk.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;