SELECT * FROM person_info AS pi, cast_info AS ci, movie_companies AS mc WHERE ci.note IN ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)') AND mc.movie_id = ci.movie_id AND ci.movie_id = mc.movie_id AND ci.person_id = pi.person_id AND pi.person_id = ci.person_id;