SELECT * FROM company_name AS cn, title AS t, movie_companies AS mc, movie_keyword AS mk, keyword AS k WHERE cn.country_code = '[us]' AND k.keyword = 'computer-animation' AND t.production_year BETWEEN 2000 AND 2010 AND t.id = mc.movie_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id AND cn.id = mc.company_id AND mc.company_id = cn.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;