SELECT * FROM keyword AS k, movie_keyword AS mk, movie_companies AS mc, title AS t WHERE k.keyword = 'character-name-in-title' AND mc.movie_id = t.id AND t.id = mc.movie_id AND t.id = mk.movie_id AND mk.movie_id = t.id AND mk.keyword_id = k.id AND k.id = mk.keyword_id AND mc.movie_id = mk.movie_id AND mk.movie_id = mc.movie_id;