SELECT * FROM comp_cast_type AS cct1, name AS n, complete_cast AS cc, cast_info AS ci, title AS t WHERE cct1.kind IN ('cast', 'crew') AND ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND n.gender = 'm' AND t.production_year > 2000 AND t.id = ci.movie_id AND ci.movie_id = t.id AND t.id = cc.movie_id AND cc.movie_id = t.id AND ci.movie_id = cc.movie_id AND cc.movie_id = ci.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND cct1.id = cc.subject_id AND cc.subject_id = cct1.id;