SELECT * FROM movie_info AS mi, complete_cast AS cc, comp_cast_type AS cct2, movie_info_idx AS mi_idx WHERE cct2.kind = 'complete+verified' AND mi.info IN ('Horror', 'Action', 'Sci-Fi', 'Thriller', 'Crime', 'War') AND mi.movie_id = mi_idx.movie_id AND mi_idx.movie_id = mi.movie_id AND mi.movie_id = cc.movie_id AND cc.movie_id = mi.movie_id AND mi_idx.movie_id = cc.movie_id AND cc.movie_id = mi_idx.movie_id AND cct2.id = cc.status_id AND cc.status_id = cct2.id;