SELECT * FROM movie_keyword AS mk, keyword AS k, cast_info AS ci WHERE ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND k.keyword IN ('murder', 'violence', 'blood', 'gore', 'death', 'female-nudity', 'hospital') AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;