SELECT * FROM title AS t, movie_companies AS mc, movie_info AS mi, info_type AS it1, movie_keyword AS mk WHERE it1.info = 'genres' AND mi.info IN ('Horror', 'Thriller') AND t.id = mi.movie_id AND mi.movie_id = t.id AND t.id = mk.movie_id AND mk.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mi.movie_id = mk.movie_id AND mk.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi.movie_id AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND it1.id = mi.info_type_id AND mi.info_type_id = it1.id;