SELECT * FROM name AS n, company_name AS cn, movie_companies AS mc, cast_info AS ci, movie_keyword AS mk WHERE ci.note IN ('(writer)', '(head writer)', '(written by)', '(story)', '(story editor)') AND cn.name LIKE 'Lionsgate%' AND mc.note LIKE '%(Blu-ray)%' AND n.gender = 'm' AND ci.movie_id = mk.movie_id AND mk.movie_id = ci.movie_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND mk.movie_id = mc.movie_id AND mc.movie_id = mk.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND cn.id = mc.company_id AND mc.company_id = cn.id;