SELECT * FROM company_name AS cn, movie_companies AS mc, title AS t, movie_info_idx AS mi_idx, info_type AS it2 WHERE cn.name LIKE 'Lionsgate%' AND it2.info = 'votes' AND mc.note LIKE '%(Blu-ray)%' AND t.production_year > 2000 AND (t.title LIKE '%Freddy%' OR t.title LIKE '%Jason%' OR t.title LIKE 'Saw%') AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mi_idx.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id AND cn.id = mc.company_id AND mc.company_id = cn.id;