SELECT * FROM movie_info_idx AS mi_idx, movie_keyword AS mk, keyword AS k, info_type AS it2 WHERE it2.info = 'votes' AND k.keyword IN ('murder', 'violence', 'blood', 'gore', 'death', 'female-nudity', 'hospital') AND mi_idx.movie_id = mk.movie_id AND mk.movie_id = mi_idx.movie_id AND it2.id = mi_idx.info_type_id AND mi_idx.info_type_id = it2.id AND k.id = mk.keyword_id AND mk.keyword_id = k.id;