SELECT * FROM movie_companies AS mc, title AS t, movie_info_idx AS mi_idx WHERE mc.note LIKE '%(Blu-ray)%' AND t.production_year > 2000 AND (t.title LIKE '%Freddy%' OR t.title LIKE '%Jason%' OR t.title LIKE 'Saw%') AND t.id = mi_idx.movie_id AND mi_idx.movie_id = t.id AND t.id = mc.movie_id AND mc.movie_id = t.id AND mi_idx.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id;