SELECT * FROM movie_info_idx AS mi_idx1, info_type AS it1, movie_companies AS mc1, company_name AS cn1 WHERE cn1.country_code = '[us]' AND it1.info = 'rating' AND it1.id = mi_idx1.info_type_id AND mi_idx1.info_type_id = it1.id AND cn1.id = mc1.company_id AND mc1.company_id = cn1.id AND mi_idx1.movie_id = mc1.movie_id AND mc1.movie_id = mi_idx1.movie_id;