SELECT * FROM link_type AS lt, movie_link AS ml, movie_info_idx AS mi_idx1, info_type AS it1, title AS t2 WHERE it1.info = 'rating' AND lt.link IN ('sequel', 'follows', 'followed by') AND t2.production_year BETWEEN 2005 AND 2008 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND t2.id = ml.linked_movie_id AND ml.linked_movie_id = t2.id AND it1.id = mi_idx1.info_type_id AND mi_idx1.info_type_id = it1.id AND ml.movie_id = mi_idx1.movie_id AND mi_idx1.movie_id = ml.movie_id;