SELECT * FROM kind_type AS kt1, title AS t1, movie_link AS ml, link_type AS lt, title AS t2, movie_companies AS mc1, company_name AS cn1 WHERE cn1.country_code = '[nl]' AND kt1.kind IN ('tv series') AND lt.link LIKE '%follow%' AND t2.production_year = 2007 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND t1.id = ml.movie_id AND ml.movie_id = t1.id AND t2.id = ml.linked_movie_id AND ml.linked_movie_id = t2.id AND kt1.id = t1.kind_id AND t1.kind_id = kt1.id AND cn1.id = mc1.company_id AND mc1.company_id = cn1.id AND t1.id = mc1.movie_id AND mc1.movie_id = t1.id AND ml.movie_id = mc1.movie_id AND mc1.movie_id = ml.movie_id;