SELECT * FROM movie_companies AS mc1, title AS t2, movie_link AS ml, kind_type AS kt2, movie_companies AS mc2, link_type AS lt, company_name AS cn1 WHERE cn1.country_code = '[nl]' AND kt2.kind IN ('tv series') AND lt.link LIKE '%follow%' AND t2.production_year = 2007 AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND t2.id = ml.linked_movie_id AND ml.linked_movie_id = t2.id AND cn1.id = mc1.company_id AND mc1.company_id = cn1.id AND ml.movie_id = mc1.movie_id AND mc1.movie_id = ml.movie_id AND kt2.id = t2.kind_id AND t2.kind_id = kt2.id AND t2.id = mc2.movie_id AND mc2.movie_id = t2.id AND ml.linked_movie_id = mc2.movie_id AND mc2.movie_id = ml.linked_movie_id;