SELECT * FROM movie_companies AS mc1, link_type AS lt, movie_link AS ml, company_name AS cn1, movie_companies AS mc2, company_name AS cn2 WHERE cn1.country_code != '[us]' AND lt.link IN ('sequel', 'follows', 'followed by') AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND cn1.id = mc1.company_id AND mc1.company_id = cn1.id AND ml.movie_id = mc1.movie_id AND mc1.movie_id = ml.movie_id AND cn2.id = mc2.company_id AND mc2.company_id = cn2.id AND ml.linked_movie_id = mc2.movie_id AND mc2.movie_id = ml.linked_movie_id;