SELECT * FROM link_type AS lt, movie_link AS ml, movie_info_idx AS mi_idx2, info_type AS it2, movie_companies AS mc2, company_name AS cn2 WHERE it2.info = 'rating' AND lt.link IN ('sequel', 'follows', 'followed by') AND mi_idx2.info < '3.5' AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND it2.id = mi_idx2.info_type_id AND mi_idx2.info_type_id = it2.id AND cn2.id = mc2.company_id AND mc2.company_id = cn2.id AND ml.linked_movie_id = mi_idx2.movie_id AND mi_idx2.movie_id = ml.linked_movie_id AND ml.linked_movie_id = mc2.movie_id AND mc2.movie_id = ml.linked_movie_id AND mi_idx2.movie_id = mc2.movie_id AND mc2.movie_id = mi_idx2.movie_id;