SELECT * FROM kind_type AS kt2, title AS t2, movie_info_idx AS mi_idx2, info_type AS it2 WHERE it2.info = 'rating' AND kt2.kind IN ('tv series', 'episode') AND mi_idx2.info < '3.5' AND t2.production_year BETWEEN 2000 AND 2010 AND it2.id = mi_idx2.info_type_id AND mi_idx2.info_type_id = it2.id AND t2.id = mi_idx2.movie_id AND mi_idx2.movie_id = t2.id AND kt2.id = t2.kind_id AND t2.kind_id = kt2.id;