SELECT * FROM movie_companies AS mc, movie_info AS mi, info_type AS it, company_type AS ct WHERE ct.kind = 'production companies' AND mc.note LIKE '%(theatrical)%' AND mc.note LIKE '%(France)%' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German') AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND ct.id = mc.company_type_id AND mc.company_type_id = ct.id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;