SELECT * FROM info_type AS it, movie_info AS mi, movie_companies AS mc WHERE mc.note NOT LIKE '%(TV)%' AND mc.note LIKE '%(USA)%' AND mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'USA', 'American') AND mc.movie_id = mi.movie_id AND mi.movie_id = mc.movie_id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;