SELECT * FROM movie_info AS mi, title AS t, info_type AS it WHERE mi.info IN ('Sweden', 'Norway', 'Germany', 'Denmark', 'Swedish', 'Denish', 'Norwegian', 'German', 'USA', 'American') AND t.production_year > 1990 AND t.id = mi.movie_id AND mi.movie_id = t.id AND it.id = mi.info_type_id AND mi.info_type_id = it.id;