SELECT * FROM info_type AS it, person_info AS pi, cast_info AS ci, movie_link AS ml, aka_name AS an, title AS t, link_type AS lt WHERE an.name LIKE '%a%' AND it.info = 'mini biography' AND lt.link = 'features' AND pi.note = 'Volker Boehm' AND t.production_year BETWEEN 1980 AND 1995 AND t.id = ci.movie_id AND ci.movie_id = t.id AND ml.linked_movie_id = t.id AND t.id = ml.linked_movie_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND it.id = pi.info_type_id AND pi.info_type_id = it.id AND pi.person_id = an.person_id AND an.person_id = pi.person_id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;