SELECT * FROM name AS n, aka_name AS an, cast_info AS ci, title AS t WHERE an.name LIKE '%a%' AND n.name_pcode_cf BETWEEN 'A' AND 'F' AND (n.gender = 'm') AND t.production_year BETWEEN 1980 AND 1995 AND n.id = an.person_id AND an.person_id = n.id AND ci.person_id = n.id AND n.id = ci.person_id AND t.id = ci.movie_id AND ci.movie_id = t.id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;