SELECT * FROM info_type AS it, person_info AS pi, aka_name AS an, cast_info AS ci, title AS t WHERE an.name LIKE '%a%' AND it.info = 'mini biography' AND pi.note = 'Volker Boehm' AND t.production_year BETWEEN 1980 AND 1984 AND t.id = ci.movie_id AND ci.movie_id = t.id AND it.id = pi.info_type_id AND pi.info_type_id = it.id AND pi.person_id = an.person_id AND an.person_id = pi.person_id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;