SELECT * FROM info_type AS it, person_info AS pi, cast_info AS ci, movie_link AS ml WHERE it.info = 'mini biography' AND pi.note = 'Volker Boehm' AND it.id = pi.info_type_id AND pi.info_type_id = it.id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;