SELECT * FROM link_type AS lt, movie_link AS ml, name AS n, cast_info AS ci WHERE lt.link = 'features' AND n.name_pcode_cf LIKE 'D%' AND n.gender = 'm' AND ci.person_id = n.id AND n.id = ci.person_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;