SELECT * FROM link_type AS lt, movie_link AS ml, title AS t, cast_info AS ci, person_info AS pi WHERE lt.link = 'features' AND pi.note = 'Volker Boehm' AND t.production_year BETWEEN 1980 AND 1984 AND t.id = ci.movie_id AND ci.movie_id = t.id AND ml.linked_movie_id = t.id AND t.id = ml.linked_movie_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;