SELECT * FROM cast_info AS ci, aka_name AS an, link_type AS lt, movie_link AS ml WHERE an.name IS NOT NULL AND (an.name LIKE '%a%' OR an.name LIKE 'A%') AND lt.link IN ('references', 'referenced in', 'features', 'featured in') AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND an.person_id = ci.person_id AND ci.person_id = an.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;