SELECT * FROM title AS t, cast_info AS ci, aka_name AS an, person_info AS pi WHERE an.name IS NOT NULL AND (an.name LIKE '%a%' OR an.name LIKE 'A%') AND pi.note IS NOT NULL AND t.production_year BETWEEN 1980 AND 2010 AND t.id = ci.movie_id AND ci.movie_id = t.id AND pi.person_id = an.person_id AND an.person_id = pi.person_id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;