SELECT * FROM link_type AS lt, movie_link AS ml, cast_info AS ci, info_type AS it, person_info AS pi WHERE it.info = 'mini biography' AND lt.link IN ('references', 'referenced in', 'features', 'featured in') AND pi.note IS NOT NULL AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND it.id = pi.info_type_id AND pi.info_type_id = it.id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;