SELECT * FROM link_type AS lt, movie_link AS ml, name AS n, cast_info AS ci WHERE lt.link IN ('references', 'referenced in', 'features', 'featured in') AND n.name_pcode_cf BETWEEN 'A' AND 'F' AND (n.gender = 'm') AND ci.person_id = n.id AND n.id = ci.person_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;