SELECT * FROM person_info AS pi, cast_info AS ci, movie_link AS ml, link_type AS lt, title AS t WHERE lt.link IN ('references', 'referenced in', 'features', 'featured in') AND pi.note IS NOT NULL AND t.production_year BETWEEN 1980 AND 2010 AND t.id = ci.movie_id AND ci.movie_id = t.id AND ml.linked_movie_id = t.id AND t.id = ml.linked_movie_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;