SELECT * FROM link_type AS lt, movie_link AS ml, title AS t, cast_info AS ci WHERE lt.link IN ('references', 'referenced in', 'features', 'featured in') AND t.production_year BETWEEN 1980 AND 2010 AND t.id = ci.movie_id AND ci.movie_id = t.id AND ml.linked_movie_id = t.id AND t.id = ml.linked_movie_id AND lt.id = ml.link_type_id AND ml.link_type_id = lt.id AND ci.movie_id = ml.linked_movie_id AND ml.linked_movie_id = ci.movie_id;