SELECT * FROM name AS n, cast_info AS ci, title AS t, person_info AS pi WHERE n.name_pcode_cf BETWEEN 'A' AND 'F' AND (n.gender = 'm') AND pi.note IS NOT NULL AND t.production_year BETWEEN 1980 AND 2010 AND n.id = pi.person_id AND pi.person_id = n.id AND ci.person_id = n.id AND n.id = ci.person_id AND t.id = ci.movie_id AND ci.movie_id = t.id AND pi.person_id = ci.person_id AND ci.person_id = pi.person_id;