SELECT * FROM movie_companies AS mc, aka_name AS an1, name AS n1, cast_info AS ci, role_type AS rt, company_name AS cn WHERE ci.note = '(voice: English version)' AND cn.country_code = '[jp]' AND mc.note LIKE '%(Japan)%' AND mc.note NOT LIKE '%(USA)%' AND n1.name LIKE '%Yo%' AND n1.name NOT LIKE '%Yu%' AND rt.role = 'actress' AND an1.person_id = n1.id AND n1.id = an1.person_id AND n1.id = ci.person_id AND ci.person_id = n1.id AND mc.company_id = cn.id AND cn.id = mc.company_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND an1.person_id = ci.person_id AND ci.person_id = an1.person_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id;