SELECT * FROM cast_info AS ci, name AS n, aka_name AS an WHERE ci.note = '(voice: English version)' AND n.name LIKE '%Yo%' AND n.name NOT LIKE '%Yu%' AND an.person_id = n.id AND n.id = an.person_id AND n.id = ci.person_id AND ci.person_id = n.id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;