SELECT * FROM cast_info AS ci, name AS n, aka_name AS an, role_type AS rt WHERE ci.note = '(voice: English version)' AND n.name LIKE '%Yo%' AND n.name NOT LIKE '%Yu%' AND rt.role = 'actress' AND an.person_id = n.id AND n.id = an.person_id AND n.id = ci.person_id AND ci.person_id = n.id AND ci.role_id = rt.id AND rt.id = ci.role_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;