SELECT * FROM movie_companies AS mc, cast_info AS ci, role_type AS rt, title AS t, name AS n1, aka_name AS a1 WHERE rt.role = 'writer' AND a1.person_id = n1.id AND n1.id = a1.person_id AND n1.id = ci.person_id AND ci.person_id = n1.id AND ci.movie_id = t.id AND t.id = ci.movie_id AND t.id = mc.movie_id AND mc.movie_id = t.id AND ci.role_id = rt.id AND rt.id = ci.role_id AND a1.person_id = ci.person_id AND ci.person_id = a1.person_id AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id;