SELECT * FROM aka_name AS an, cast_info AS ci, char_name AS chn, role_type AS rt, movie_companies AS mc WHERE ci.note = '(voice)' AND mc.note LIKE '%(200%)%' AND (mc.note LIKE '%(USA)%' OR mc.note LIKE '%(worldwide)%') AND rt.role = 'actress' AND ci.movie_id = mc.movie_id AND mc.movie_id = ci.movie_id AND ci.role_id = rt.id AND rt.id = ci.role_id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;