SELECT * FROM cast_info AS ci, aka_name AS an, name AS n, char_name AS chn, title AS t WHERE ci.note = '(voice)' AND n.gender = 'f' AND n.name LIKE '%Angel%' AND t.production_year BETWEEN 2007 AND 2010 AND ci.movie_id = t.id AND t.id = ci.movie_id AND n.id = ci.person_id AND ci.person_id = n.id AND chn.id = ci.person_role_id AND ci.person_role_id = chn.id AND an.person_id = n.id AND n.id = an.person_id AND an.person_id = ci.person_id AND ci.person_id = an.person_id;